import { useEffect, useState } from "react";

// src/data.js
function Data() {
  const [data, setData] = useState([]);

  const fetchData = () => {
    axios
      .get('http://localhost:6001/api/data')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleApprove = (id) => {
    axios
      .put(`http://localhost:6001/approve/${id}`)
      .then((response) => {
        fetchData();
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleDisapprove = (id) => {
    axios
      .put(`http://localhost:6001/disapprove/${id}`)
      .then((response) => {
        fetchData();
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="p-4">
      <h1 className="bg-blue-500 text-white p-4 mb-4 rounded-lg text-2xl font-bold mb-4 text-center">Visitors Names</h1>
      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2">Visitor Name</th>
            <th className="p-2">Visitor Email</th>
            <th className="p-2">Status</th>
            <th className="p-2">Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id} className="border-t">
              <td className="p-2 text-center">{item.name}</td>
              <td className="p-2 text-center">{item.email}</td>
              <td className="p-2 text-center">{item.approved ? 'Approved' : 'Pending'}</td>
              <td className="p-2 text-center">
                <button
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-2 text-center"
                  onClick={() => handleApprove(item._id)}
                >
                  Approve
                </button>
                <button
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded text-center"
                  onClick={() => handleDisapprove(item._id)}
                >
                  Disapprove
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Data;
